import React from 'react';
import ReactDOM from 'react-dom';
import './style.css';


function App() {
    let tod = new Date(2018, 11, 22, 2);
    let hour = tod.getHours();
    let printstat;

    const styles = {

        fontFamily: "Ubuntu",
        margin: "20vw"
    }

    if(hour > 0 && hour < 12) {
        printstat = "Morning Dude";
        styles.color = ""
    }

    else if(hour >= 12 && hour < 17) {
        printstat = "Noon bro";
    }

    else if(hour >= 17 && hour < 19) {
        printstat = "Evening Fella";
    }

    else {
        printstat = "Night night!";
    }



    return(<h1 style={styles}>{printstat}</h1>);
}



ReactDOM.render(<App />, document.getElementById('root'));
